CREATE DATABASE IF NOT EXISTS `enoteca`;
USE enoteca;

DROP USER IF EXISTS 'gestore_enoteca';
CREATE USER 'gestore_enoteca' IDENTIFIED BY 'Password-Molto-Sicura';
